    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        usuarioA = usuario.getText();
        contraseniaA = contrasenia.getText();
        Controlador contra = new Controlador();
        boolean control = contra.acceso(usuarioA, contraseniaA);
        System.out.println(control);
        System.out.println(usuarioA);
        System.out.println(contraseniaA);
        if (control == true) {
            java.awt.EventQueue.invokeLater(new Runnable() {
                public void run() {
                    new ListaBD().setVisible(true);
                }
            });
            dispose();
        } else {
            JOptionPane.showMessageDialog(null, "Las credenciales son incorrectas.");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

